class TestAgents:
    """
    https://docs.griptape.ai/en/latest/griptape-framework/structures/agents/
    """

    def test_agents(self):
        from griptape.tools import Calculator
        from griptape.structures import Agent

        agent = Agent(tools=[Calculator()])

        result = agent.run("what's 123^312?")

        assert result.output is not None
        answer = ""
        assert answer in result.output.to_text()
